import React, {Component} from "react";
import p5 from "p5";

class Sketch extends React.Component {
  state = {
    count: '0'
  }
  constructor(props) {
    super(props); // THIS CALLS THE BASE CLASS CONSTRUCTOR
    this.state = {
      message: 'Welcome',
      ingredients : []
    }
    this.myRef = React.createRef();

  }
  Sketch = (p) => {
    let queue = [];
    let keywords = [];
    let apiKey = "api-key=QAxdBlc0xuqLooRSPDBfuLaec4GwdRhU";
