import p5 from 'p5'
let x = 1;
let input,button,query,filter,greeting;
let emailedPeriod = 1;  // 7, 30 most emailed
let apiKey = 'api-key=QAxdBlc0xuqLooRSPDBfuLaec4GwdRhU'
let mostEmailed = 'https://api.nytimes.com/svc/mostpopular/v2/emailed/1.json?' + apiKey

let url = 'https://api.nytimes.com/svc/mostpopular/v2/shared/1/facebook.json?'+ apiKey

class Sketch extends React.Component {
  constructor(props) {
    super(props)
    this.myRef = React.createRef()
  }

  Sketch = (p) => {
    p.preload = () => {
    loadArticles();
    }

    const loadArticles = (mostEmail,gotData) => {
    p.loadJSON(mostEmailed, gotData);
  }



    const createSearch = () => {
      input = p.createInput('', 'text');
      greeting = p.createElement('h1', 'What is the latest jargon?');
      greeting.position(1100, -20);
      button = p.createButton('submit');
      input.position(800, 60);
    }
    p.setup = () => {
      p.createCanvas(p.windowWidth, p.windowHeight / 2.5);
    }

    function gotData(data) {
   var results = data.results;

      for (var i = 0; i < results.length/3; i++) {
         const headline = p.createElement('p','  : ' + results[i].title);

}
    }

    p.draw = () => {

       drawCircles()
    }

    function drawCircles() {
      p.background(245, 100, 104);
      p.stroke(127, 19, 120);
      p.fill(39, 24, 79);
      p.stroke(127, 63, 120);
      p.circle(p.width / 2 - (x), p.height / 2, x); // left
      p.circle(p.width / 2, p.height / 2, x - (.8 * x)); // middle
      p.circle(p.width / 2 + (x), p.height / 2, x + 90); // right
      x += 1.5;
    }

  }
  componentDidMount() {
    this.myP5 = new p5(this.Sketch, this.myRef.current)
  }

  render() {
    return (<div ref={this.myRef}></div>)
  }
}

export default Sketch
